import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ExpenseService } from '../services/expense.service';

@Component({
  selector: 'app-owe-expense',
  templateUrl: './owe-expense.component.html',
  styleUrls: ['./owe-expense.component.css']
})
export class OweExpenseComponent implements OnInit {
  expenseId!:number
  expenseDetail:any
  constructor(private route:ActivatedRoute,private expenseService:ExpenseService){}
  ngOnInit(): void {
    this.route.params.subscribe((param)=>{
      this.expenseId=param['expenseId'];
    },(err)=>{
      alert('Something went wrong');
    })
    this.expenseService.viewMyOweExpenseDetails(this.expenseId).subscribe((res)=>{
      this.expenseDetail=res;
      console.log(res);
    },(err)=>{
      alert("Something went wrong");
    })    
  }
}
<div class="card text-center">
    <div class="card-header">
        {{expenseDetail.expenseName}}
    </div>
    <div class="card-body">
      <h5 class="card-title">Amount of expense: {{expenseDetail.expenseAmount | currency:'INR'}}</h5>
      <h6 class="card-title">Amount You have to pay: {{expenseDetail.amountOwe | currency}}</h6>
      <p class="card-text">{{expenseDetail.description}}</p>

      <a href="#" class="btn btn-primary">Go somewhere</a>
    </div>
    <div class="card-footer text-muted">
      Created Date: {{expenseDetail.expenseDate}}
    </div>
  </div>
