import { Component, OnInit } from '@angular/core';
import { FormArray, FormControl, FormGroup, Validators } from '@angular/forms';
import { GroupService } from '../services/group.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-add-expense',
  templateUrl: './add-expense.component.html',
  styleUrls: ['./add-expense.component.css']
})
export class AddExpenseComponent implements OnInit {
  expenseForm!:any
  usersInGroup!:any
  groupId!:number
  constructor(private groupService:GroupService,private route:ActivatedRoute){}
  ngOnInit(): void {
    this.expenseForm=new FormGroup({
      expenseName:new FormControl(null,[Validators.required]),
      expenseDescription:new FormControl(null,[Validators.required]),
      expenseAmount:new FormControl(null,[Validators.required]),
      paidBy:new FormArray([]),
      splitAmong:new FormArray([])
    });
    this.route.params.subscribe((param)=>{
      this.groupId=param['groupId'];
    });
    this.groupService.getUserInGroup(this.groupId).subscribe((data)=>{
      this.usersInGroup=data;
    },(err)=>{
      console.log(err);
    });
  }
  onAddPaid(){
    const emailControl=new FormControl(null);
    (this.expenseForm.controls['paidBy'] as FormArray).push(emailControl);
  }
  onAddSplit(){
    const emailControl=new FormControl(null);
    (this.expenseForm.controls['splitAmong'] as FormArray).push(emailControl);
  }
  onSubmit(){
    console.log(this.expenseForm);
  }
}
