import { Component, OnInit } from '@angular/core';
import { UserStoreService } from '../services/user-store.service';
import { UserServiceService } from '../services/user-service.service';
import { Router } from '@angular/router';
import { switchMap } from 'rxjs';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css'],
})
export class NavbarComponent implements OnInit {
  email!: any;
  amountAfterLogin!:any;
  updatedAmount:any
  AmountduringTraverse:any
  actualAmount:any
  constructor(
    private userStoreService: UserStoreService,
    private useService: UserServiceService,
    private router:Router
  ) {}
  ngOnInit(): void {
    this.userStoreService.getEmailFromStore().subscribe((data) => {
      this.email = data || this.useService.getEmailFromToken();
      this.amountAfterLogin=this.useService.getAmountFromToken();

    });
    // this.useService.getUserById().subscribe((res)=>{
    //   this.updatedAmount=res.amount;
    // },(err)=>{
    //   console.log(err);
    // })
    // this.userStoreService.getUserDetailFromStore().subscribe((res)=>{
    //   this.realAmount=res.amount || this.updatedAmount || this.amount;
    // })
    this.useService.getUserById().pipe(
      switchMap((res)=>{
        this.updatedAmount=res.amount;
        return this.userStoreService.getUserDetailFromStore();
      })
    ).subscribe((res)=>{
      this.AmountduringTraverse=res?.amount || this.updatedAmount || this.amountAfterLogin;
    })
    this.actualAmount=this.AmountduringTraverse || this.amountAfterLogin;
  }
  onLogout(){
    localStorage.clear();
    this.router.navigateByUrl("");
  }
}
<nav class="navbar navbar-expand-lg navbar-light custom-navbar">
  <div class="container-fluid">
    <a class="navbar-brand" href="" (click)="onLogout()"
      >Expense Settlement Application</a
    >
    <button
      class="navbar-toggler"
      type="button"
      data-bs-toggle="collapse"
      data-bs-target="#navbarSupportedContent"
      aria-controls="navbarSupportedContent"
      aria-expanded="false"
      aria-label="Toggle navigation"
    >
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarSupportedContent">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item">
          <a
            (click)="onLogout()"
            href=""
            class="nav-link active"
            aria-current="page"
            >Home</a
          >
        </li>
        <li class="nav-item">
          <a
            *ngIf="email"
            routerLink="/dashboard"
            class="nav-link active"
            aria-current="page"
            >Dashboard</a
          >
        </li>

        <li class="nav-item">
          <a
            *ngIf="email"
            routerLink="/dashboard"
            class="nav-link active"
            aria-current="page"
            >Welcome {{ email }}</a
          >
        </li>
        <li class="nav-item">
          <a
            *ngIf="email"
            class="nav-link active"
            aria-current="page"
            routerLink="/add new group"
            >Create New Group</a
          >
        </li>
        <li class="nav-item">
          <a
            *ngIf="actualAmount"
            class="nav-link active"
            aria-current="page"
            routerLink=""
            >Amount: {{ actualAmount | currency : "INR" }}
          </a>
        </li>
        <li class="nav-item">
          <a
            *ngIf="email"
            class="nav-link active"
            href=""
            aria-current="page"
            (click)="onLogout()"
            >Logout</a
          >
        </li>
      </ul>
    </div>
  </div>
</nav>
