import { Component, OnInit } from '@angular/core';
import { UserServiceService } from '../services/user-service.service';
import { ActivatedRoute, Router } from '@angular/router';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-update-user',
  templateUrl: './update-user.component.html',
  styleUrls: ['./update-user.component.css'],
})
export class UpdateUserComponent implements OnInit {
  userDetail: any;
  userId: any;
  userForm!: FormGroup;
  constructor(
    private userService: UserServiceService,
    private route: ActivatedRoute,
    private router: Router
  ) {}
  ngOnInit(): void {
    this.route.params.subscribe((param) => {
      this.userId = param['id'];
    });
    this.userService.getUserIdForAdmin(this.userId).subscribe(
      (res) => {
        this.userDetail = res;
        this.initializeForm(res);
      },
      (err) => {
        alert('Something went wrong');
      }
    );

  }
  initializeForm(){
    this.userForm = new FormGroup({
      email: new FormControl(this.userDetail.email, [Validators.email]),
      userName: new FormControl(this.userDetail.userName, [
        Validators.required,
      ]),
      amount: new FormControl(this.userDetail.amount, [Validators.required]),
    });
  }
  onUpdate() {
    const user = this.userForm.value;
    this.userService.updateUser(user).subscribe(
      (res) => {
        this.router.navigateByUrl('/dashboard');
      },
      (err) => {
        alert('Something went wrong!');
      }
    );
  }
}
<div class="base">
    <div class="login">
      <form [formGroup]="userForm" (ngSubmit)="onUpdate()">
        <div class="mb-3">
          <label for="exampleInputEmail1" class="form-label">Email address</label>

          <input
            type="email"
            class="form-control"
            id="exampleInputEmail1"
            placeholder="Enter your Email"
            aria-describedby="emailHelp"
            formControlName="email"
          />
          <span *ngIf="(userForm.controls['email'].touched || userForm.controls['email'].dirty) && userForm.controls['email'].invalid" style="color: red;">Enter correct Email</span>
        </div>
        <div class="mb-3">
            <label for="exampleInputUserName1" class="form-label">UserName</label>

            <input
              type="text"
              class="form-control"
              id="exampleInputUserName1"
              placeholder="Enter your UserName"
              aria-describedby="emailHelp"
              formControlName="userName"
            />
            <span *ngIf="(userForm.controls['userName'].touched || userForm.controls['userName'].dirty) && userForm.controls['userName'].invalid" style="color: red;">UserName is required</span>
          </div>
          <div class="mb-3">
            <label for="exampleInputUserAmount1" class="form-label">Amount</label>

            <input
              type="text"
              class="form-control"
              id="exampleInputUserAmount1"
              placeholder="Enter your Amount"
              aria-describedby="emailHelp"
              formControlName="amount"
            />
            <span *ngIf="(userForm.controls['amount'].touched || userForm.controls['amount'].dirty) && userForm.controls['amount'].invalid" style="color: red;">Amount is required</span>
          </div>

        <button type="submit" class="btn btn-primary" [disabled]="userForm.invalid">Submit</button>
      </form>
    </div>
  </div>
