import { Component, OnInit } from '@angular/core';
import { FormArray, FormControl, FormGroup } from '@angular/forms';
import { GroupService } from '../services/group.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-add-expense',
  templateUrl: './add-expense.component.html',
  styleUrls: ['./add-expense.component.css']
})
export class AddExpenseComponent implements OnInit {
  expenseForm!:any
  usersInGroup!:any
  groupId!:number
  constructor(private groupService:GroupService,private route:ActivatedRoute){}
  ngOnInit(): void {
    this.expenseForm=new FormGroup({
      expenseName:new FormControl(null),
      expenseDescription:new FormControl(null),
      expenseAmount:new FormControl(null),
      paidBy:new FormArray([])
    });
    this.route.params.subscribe((param)=>{
      this.groupId=param['groupId'];
    });
    this.groupService.getUserInGroup(this.groupId).subscribe((data)=>{
      this.usersInGroup=data;
    },(err)=>{
      console.log(err);
    });
  }
  onAdd(){
    const emailControl=new FormControl(null);
    (this.expenseForm.controls['paidBy'] as FormArray).push(emailControl);
  }
  onSubmit(){
    console.log(this.expenseForm);
  }
}
<div class="base">
    <div class="login">
      <form [formGroup]="expenseForm" (ngSubmit)="onSubmit()">
        <div class="mb-3">
          <label for="exampleInputName" class="form-label">Expense Name</label>

          <input
            type="text"
            class="form-control"
            id="exampleInputName"
            placeholder="Enter your Expense Name"
            formControlName="expenseName"
          />
          <!-- <span *ngIf="(groupForm.controls['groupName'].touched || groupForm.controls['groupName'].dirty) && groupForm.controls['groupName'].invalid" style="color: red;">Required Field</span> -->
        </div>
        <div class="mb-3">
            <label for="exampleInputDescription" class="form-label">Expense Description</label>

            <input
              type="text"
              class="form-control"
              id="exampleInputDescription"
              placeholder="Enter your Expense Description"
              formControlName="expenseDescription"
            />
            <!-- <span *ngIf="(groupForm.controls['description'].touched || groupForm.controls['description'].dirty) && groupForm.controls['description'].invalid" style="color: red;">Required Field</span> -->
          </div>
          <div class="mb-3">
            <label for="exampleInputAmount" class="form-label">Expense Amount</label>

          <input
            type="number"
            class="form-control"
            id="exampleInputAmount"
            placeholder="Enter your Expense Amount"
            formControlName="expenseAmount"
          />
          </div>
          <div class="mb-3" formArrayName="paidBy">
            <div *ngFor="let paid of expenseForm.controls['paidBy'].controls; let i=index;">
              <select>
                <option></option>
              </select>
            </div>
          </div>
          <!-- <span *ngIf="(groupForm.controls['description'].touched || groupForm.controls['description'].dirty) && groupForm.controls['description'].invalid" style="color: red;">Required Field</span> -->

        <button type="button" class="btn btn-primary" (click)="onAdd()">Add Email</button>
        <button type="submit" class="btn btn-primary" >Submit</button>
      </form>
    </div>
  </div>
