import { Component, OnInit } from '@angular/core';
import { UserServiceService } from '../services/user-service.service';
import { ActivatedRoute } from '@angular/router';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-update-user',
  templateUrl: './update-user.component.html',
  styleUrls: ['./update-user.component.css']
})
export class UpdateUserComponent implements OnInit{
  userDetail:any
  userId:any
  userForm!:FormGroup
  constructor(private userService:UserServiceService,private route:ActivatedRoute){}
  ngOnInit(): void {
    this.route.params.subscribe((param)=>{
      this.userId=param['id'];
    })
    this.userService.getUserIdForAdmin(this.userId).subscribe((res)=>{
      this.userDetail=res;
    },(err)=>{
      alert("Something went wrong");
    })
    this.userForm=new FormGroup({
      email:new FormControl(this.userDetail.email,[Validators.email]),
      userName:new FormControl(this.userDetail.userName,[Validators.required]),
      amount:new FormControl(this.userDetail.amount,[Validators.required])
    })
  }


}
